$(document).ready(function() {
    const chatMessages = $('#chat-messages');
    const userMessage = $('#user-message');
    const sendButton = $('#send-button');

    // sendButton.on('click', function() {
    //     const message = userMessage.val();
    //     if (message.length === 0) {
    //         return;
    //     } else {
    //         // sendMessageToServer(message);
    //         displayUserMessage(message);
    //         userMessage.val('');
    //         // Прокрутить чат в самый низ после отправки сообщения
    //         chatMessages.scrollTop(chatMessages[0].scrollHeight);
    //     }
    // });

    userMessage.on('keydown', function(event) {
        if (event.key === 'Enter') {
            event.preventDefault();
            sendButton.click(); // Отправить сообщение при нажатии Enter
        }
    });



    function displayUserMessage(message) {
        const messageElement = $('<div>').addClass('chat-message user').text('You: ' + message);
        chatMessages.append(messageElement);
        // Прокрутить чат в самый низ после добавления сообщения
        chatMessages.scrollTop(chatMessages[0].scrollHeight);
    }

    function displayBotMessage(message) {
        const messageElement = $('<div>').addClass('chat-message sent').text(message);
        chatMessages.append(messageElement);
        // Прокрутить чат в самый низ после добавления сообщения
        chatMessages.scrollTop(chatMessages[0].scrollHeight);
    }


    function getCookie(name) {
        const cookieValue = document.cookie.match('(^|;)\\s*' + name + '\\s*=\\s*([^;]+)');
        return cookieValue ? cookieValue.pop() : '';
    }
});